import { Container, FormControl, FormLabel } from '@chakra-ui/react'
import React, { useRef } from 'react'

interface formProps {
    onAdd: (title: string) => void
}

export default function NewTodo(props: formProps) {
    const titleRef = useRef<HTMLInputElement>(null)
    function submitHandler(e: React.FormEvent) {
        e.preventDefault()
        const title = titleRef.current!.value
        title && props.onAdd(title)
    }
    return (
        <Container>
            <form action="" onSubmit={submitHandler}>
                <FormControl>
                    <FormLabel htmlFor="Todo">Title</FormLabel>
                    <input type="text" id='Todo' ref={titleRef} />
                </FormControl>
                <button>Create</button>
            </form>
        </Container>
    )
}
